package com.day4.operators;

public class UnaryOperatosDemo3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a  = 20;
		
		System.out.println(a++); // 20, a=21
		System.out.println(++a); // 22  a=22
		System.out.println(a--); // 22  a=21
		System.out.println(--a); // 20

	}

}
