package branchingstmsts;

public class ContinueDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	for(int i=1; i<=10; i++)	{
		if(i==6) 
			continue;
		
	System.out.print(i + " ");
	}
		
	
	
	}

}
// continue will skip one iteration and will excute the rest..