package icraftKnowledgeJavaExersice;

public class Expressions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	// TODO Auto-generated method stub
		
		// expression = operands & operators
		// operands = value, variables, numbers, quantity
		// operators = + - * / %
		
		int friends = 10; // if i want to add more value to my variable im gonna do
		
		friends = friends / 3 ;// i can do -, *, % if we
		//cast our resul in to decimal portion so we gonna do friends = (double) friends/ 3
		
		System.out.println(friends);
		
	// operators = + - * / %
		
		double friends1 = 11; // if i want to add more value to my variable im gonna do
		
		friends = (int) ((double) friends1 / 3) ;  // i can do -, *, % if we
		//cast our resul in to decimal portion so we gonna do friends = (double) friends/ 3
		
		System.out.println(friends1);
		
		int myfriend = 9;
		myfriend ++;// i can do increment or decrement by -- double minus sign
		System.out.println(myfriend);
	}

}
