package icraftKnowledgeJavaExersice;

public class StringSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name = "coke";
		
		switch(name) {
		
		case "mango":
			System.out.println(name +" is fruit");
		break;
		
		case "tomato":
			System.out.println(name + " is vegitable");
		break;
		
		case "coke":
			System.out.println(name + " is cold drink");
		break; 
		
		default:
			System.out.println("other fruit or vegitable or cold");
			
			
		
		
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
